import React, { Component } from 'react';
import ReactMapboxGl, { Layer, Feature } from "react-mapbox-gl";
import axios from 'axios';

const Mapbox = ReactMapboxGl({
  accessToken: 'pk.eyJ1Ijoic2FzY2hhbXoiLCJhIjoiY2pueWE3NGN0MGR0YjNwbzV3Zm5udjFkZiJ9.Yhe8XyJjVRuDsBHs9SsBMw',
});

class Map extends Component {

  constructor(props) {
    super(props);

    this.state = {
      markers: []
    };
  }

  componentDidMount() {
    this.requestWebcams();
  }

  requestWebcams(x, y, limit = 50, offset = 0) {
    axios.get(`https://webcamstravel.p.mashape.com/webcams/list/limit=${limit}/country=SE&show=webcams:player,location`, {
      headers: {
        'X-Mashape-Key': 'dEOco1PlZlmshIPqrt4ycn7xZE9zp1Mscwbjsnu53Bztt9kFJ2',
        'X-Mashape-Host': 'webcamstravel.p.mashape.com',
      }
    }).then(res => {
      console.log(res.data);
      const { webcams } = res.data.result;
      console.log('Webcams: ', webcams);
      this.setState(prevState => ({...prevState, markers: [...prevState.markers, ...webcams]}));
    }); 
  }

  componentWillUpdate() {
    console.log(this.state.markers);
  }

  handleClick(_, evt) {
    console.log(evt);
    const { lng, lat } = evt.lngLat;
    axios.get(`https://api.mapbox.com/geocoding/v5/mapbox.places/${lng},${lat}.json?access_token=pk.eyJ1Ijoic2FzY2hhbXoiLCJhIjoiY2pueWE3NGN0MGR0YjNwbzV3Zm5udjFkZiJ9.Yhe8XyJjVRuDsBHs9SsBMw&country`)
      .then(res => {
        console.log(res.data);
        const countryCode = res.data.features.filter(stat => stat.id.match(/country/))[0].properties.short_code;
      });

  }

  renderMarker({ location, player }) {
    const src = player.live.embed || player.day.embed;
    const { longitude, latitude, city, country } = location;

    return (
      <Feature onClick={() => this.props.updateFeed({ src, city, country })} coordinates={[longitude, latitude]}/>
    );
  }

  render() {
    const { markers } = this.state;

    return (
      <div>
        <Mapbox
          style='mapbox://styles/mapbox/streets-v8'
          containerStyle={{
            height: "100vh",
            width: "100vw"
          }}
          onClick={this.handleClick.bind(this)}
        >
          <Layer
            type="symbol"
            id="marker"
            layout={{ "icon-image": "marker-15" }}>
            { markers.map(marker => this.renderMarker(marker)) }
          </Layer>
        </Mapbox>
      </div>
    );
  }
}

export default Map;
