import React, { Component } from 'react';
import './App.css';
import Map from './Map';
import Feed from './Feed';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      accessToken: null,
      feed: []
    };

    this.updateFeed = this.updateFeed.bind(this);
  }

  componentDidMount() {
    const [, accessToken] = window.location.hash.split('=');
    if (accessToken) this.setState(oldState => ({ ...oldState, accessToken }));
  }

  updateFeed({lng, lat}) {
    const { accessToken } = this.state;
    const locationSearch = 'https://api.instagram.com/v1/locations/search?lat=48.858844&lng=2.294351';
    // `https://api.instagram.com/v1/media/search?lat=48.858844&lng=2.294351&distance=5000&access_token=${accessToken}`
    fetch(locationSearch + '&access_token=' + accessToken)
      .then(res => res.json())
      .then(res => res.data[0])
      .then(res => {
        fetch(`https://api.instagram.com/v1/locations/${res.id}/media/recent?access_token=${accessToken}`)
          .then(res => res.json())
          .then(res => console.log(res));
      })
  }

  render() {
    const { accessToken, feed } = this.state;

    return (
      <div className="App">
        { accessToken ? 
        <>
          <Feed token={accessToken} feed={feed}/>
          <Map updateFeed={this.updateFeed}/>
        </> : 
          <a href='https://api.instagram.com/oauth/authorize/?client_id=e600c57782324134a715ec31f4b5b20c&scope=public_content&redirect_uri=http://127.0.0.1:3000&response_type=token'>Log in with IG</a>
        }
      </div>
    );
  }
}

export default App;
