import React, { Component } from 'react';
import './App.css';
import Map from './Map';
import Feed from './Feed';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      renderFeed: false,
      feedData: null
    };

    this.updateFeed = this.updateFeed.bind(this);
  }

  componentDidMount() {
    const [, accessToken] = window.location.hash.split('=');
    if (accessToken) this.setState(oldState => ({ ...oldState, accessToken }));
  }

  updateFeed({ src, city, country }) {
    this.setState(prevState => ({ ...prevState, renderFeed: true, feedData: { src, city, country } }));
  }

  render() {
    const { renderFeed, feedData } = this.state;

    return (
      <div className="App">
        { renderFeed && <Feed data={feedData} />}
        <Map updateFeed={this.updateFeed} />
      </div>
    );
  }
}

export default App;
