import React, { Component } from 'react';
import ReactMapboxGl, { Layer, Feature } from "react-mapbox-gl";
import axios from 'axios';

const Mapbox = ReactMapboxGl({
  accessToken: 'pk.eyJ1Ijoic2FzY2hhbXoiLCJhIjoiY2pueWE3NGN0MGR0YjNwbzV3Zm5udjFkZiJ9.Yhe8XyJjVRuDsBHs9SsBMw',
});

class Map extends Component {

  constructor(props) {
    super(props);

    this.state = {
      markers: []
    };
  }

  componentDidMount() {
    this.requestWebcams();
  }

  requestWebcams(x, y, limit = 50, offset = 0) {
    axios.get(`https://webcamstravel.p.mashape.com/webcams/list/limit=${limit}/offset=${offset}/country=SE&show=webcams:player,location`, {
      headers: {
        'X-Mashape-Key': 'dEOco1PlZlmshIPqrt4ycn7xZE9zp1Mscwbjsnu53Bztt9kFJ2',
        'X-Mashape-Host': 'webcamstravel.p.mashape.com',
      }
    }).then(res => {
      console.log(res.data);
      const { webcams } = res.data.result;
      console.log('Webcams: ', webcams);
      this.setState(prevState => ({...prevState, markers: [...prevState.markers, ...webcams]}));
    }); 
  }

  componentWillUpdate() {
    console.log(this.state.markers);
  }

  handleClick(_, evt) {
    const { lng, lat } = evt.lngLat;
  }

  renderMarker({ lng, lat }) {
    return (
      <Layer
        type="symbol"
        id="marker"
        layout={{ "icon-image": "marker-15" }}>
        <Feature coordinates={[lng, lat]} />
      </Layer>
    );
  }

  render() {
    const { markers } = this.state;

    return (
      <div>
        <Mapbox
          ref="mapbox"
          style='mapbox://styles/mapbox/streets-v9'
          containerStyle={{
            height: "100vh",
            width: "100vw"
          }}
          onClick={this.handleClick.bind(this)}
        >
        {/* { markers.map(marker => this.renderMarker(marker.location.longitude, marker.location.latitude)) } */}
        </Mapbox>
      </div>
    );
  }
}

export default Map;
