import React, { Component } from 'react';
import './App.css';
import Map from './Map';
import Feed from './Feed';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      apiKey: null
    }
  }

  componentDidMount() {
    const [, key] = window.location.hash.split('=');
    if (key) console.log(key);
  }

  render() {
    const { apiKey } = this.state;

    return (
      <div className="App">
      { apiKey
        ? <><Feed apiKey={this.state.apiKey}/><Map /></> : 
        <a href='https://api.instagram.com/oauth/authorize/?client_id=e600c57782324134a715ec31f4b5b20c&redirect_uri=http://127.0.0.1:3000&response_type=token'>Log in with IG</a>
      }

        {/* <Feed />
        <Map /> */}
      </div>
    );
  }
}

export default App;
