import React, { Component } from 'react';
import './App.css';
import Map from './Map';
import Feed from './Feed';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      src: null
    };

    this.updateFeed = this.updateFeed.bind(this);
  }

  componentDidMount() {
    const [, accessToken] = window.location.hash.split('=');
    if (accessToken) this.setState(oldState => ({ ...oldState, accessToken }));
  }

  updateFeed(src) {
    this.setState(prevState => ({ ...prevState, src }));
  }

  render() {
    const { src } = this.state;

    return (
      <div className="App">
        <Feed src={src} />
        <Map updateFeed={this.updateFeed} />
      </div>
    );
  }
}

export default App;
