import React, { Component } from 'react';
import './App.css';
import Map from './Map';
import Feed from './Feed';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      accessToken: null
    }
  }

  componentDidMount() {
    const [, accessToken] = window.location.hash.split('=');
    if (accessToken) this.setState({ accessToken });
  }

  render() {
    const { accessToken } = this.state;

    return (
      <div className="App">
        { accessToken
          ? <><Feed token={this.state.accessToken}/><Map /></> : 
          <a href='https://api.instagram.com/oauth/authorize/?client_id=e600c57782324134a715ec31f4b5b20c&redirect_uri=http://127.0.0.1:3000&response_type=token'>Log in with IG</a>
        }
      </div>
    );
  }
}

export default App;
